                                    angular.forEach(results, function(mval, ind) {
                                        angular.forEach(data, function(val) {
                                            if(mval.text == val.text){
                                                results.splice(ind, 1);
                                            }
                                        });
                                    });
                                    angular.forEach(results, function (val) {
                                        if (!uniqueAdd[val.text]) {
                                            newarrAdd.push(val);
                                            uniqueAdd[val.text] = val;
                                        }
                                    });
