                    "from":$scope.startVacancyDate,
                    "to":$scope.endDate,
                    "types":null,
                    "vacancyId":null,
                    "vacancyStatuses": $scope.vacancysStatusesParam,
                    "interviewStatuses":$scope.inVacancysStatusesParam,
                    "interviewCreatorIds": $scope.choosenPersons,
                    "withCandidates": true,
