function firstLetters(string){
    var array = [];
    var word1 = [];
    var word2 = [];
    var words = string.split(" ");
    word1.push(words[0]);
    var letter1 = word1[0].split("");
    array.push(letter1[0]);
    if(words.length > 1){
        word2.push(words[1]);
        var letter2 = word2[0].split("");
        array.push(letter2[0]);
    }
    var acronym = array.join('');
    return acronym
}

